import { useState } from "react";

function Square({squares, setSquares, row, col, prev, setPrev}) {

  const i = (3 * row) + col;

  function handleClick() {
    console.log(i);

    if (Array.isArray(squares) && !squares[i]) {

      const newSquares = squares.slice();
      if (prev === "X") {
        newSquares[i] = "O";
        setPrev("O");
      } else {
        newSquares[i] = "X";
        setPrev("X");
      }

      setSquares(newSquares);

    }

    console.log("clicked!");
  }

  return (
    <button 
      className="square" 
      onClick = {handleClick}
    >

      {squares[i]}
    </button>
  );

}

function BoardRow({squares, setSquares, row, prev, setPrev}) {

  return (
    <div style = {{display: "flex"}}>
      <Square squares = {squares} setSquares = {setSquares} row = {row} col = {0} prev = {prev} setPrev = {setPrev} />
      <Square squares = {squares} setSquares = {setSquares} row = {row} col = {1} prev = {prev} setPrev = {setPrev} />
      <Square squares = {squares} setSquares = {setSquares} row = {row} col = {2} prev = {prev} setPrev = {setPrev} />
    </div>
  );

}

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [prev, setPrev] = useState("O");

  return (
    <>
      <BoardRow squares = {squares} setSquares = {setSquares} row = {0} prev = {prev} setPrev = {setPrev} />
      <BoardRow squares = {squares} setSquares = {setSquares} row = {1} prev = {prev} setPrev = {setPrev} />
      <BoardRow squares = {squares} setSquares = {setSquares} row = {2} prev = {prev} setPrev = {setPrev} />
    </>
  );

}
