import { useState } from "react";

function Square({squares, row, col, prev}) {

  const i = (3 * row) + col - 1;

  function handleClick(squares, prev, i) {
    console.log(i);

    if(!squares[i]) {

      if(prev === "X") {
        squares[i] = "O";
        setPrev("O");
      }
      else {
        squares[i] = "X";
        setPrev("X");
      }

    }

    console.log("clicked!");
  }

  return (
    <button 
      className="square" 
      onClick = {() => {handleClick}}
    >

      {squares[i]}
    </button>
  );

}

function BoardRow({squares, row, prev}) {

  return (
    <div style = {{display: "flex"}}>
      <Square squares = {squares} row = {row} col = {1} prev = {prev} />
      <Square squares = {squares} row = {row} col = {2} prev = {prev} />
      <Square squares = {squares} row = {row} col = {3} prev = {prev} />
    </div>
  );

}

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [prev, setPrev] = useState("O");

  return (
    <>
      <BoardRow squares = {squares} row = {1} prev = {prev} />
      <BoardRow squares = {squares} row = {2} prev = {prev} />
      <BoardRow squares = {squares} row = {3} prev = {prev} />
    </>
  );

}
