import { useState } from "react";

function Square({squares, row, col}) {

  const i = (3 * row) + col - 1;
  console.log(i);

  function handleClick(squares) {
    if(!squares[i]) {
      if(prev === "X") squares[i] = "O";
    }
    console.log("clicked!");
  }

  return (
    <button 
      className="square" 
      onClick = {handleClick}
    >

      {squares[i]}
    </button>
  );

}

function BoardRow({squares, row}) {

  return (
    <div style = {{display: "flex"}}>
      <Square squares = {squares} row = {row} col = {1} />
      <Square squares = {squares} row = {row} col = {2} />
      <Square squares = {squares} row = {row} col = {3} />
    </div>
  );

}

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));

  return (
    <>
      <BoardRow squares = {squares} row = {1} />
      <BoardRow squares = {squares} row = {2} />
      <BoardRow squares = {squares} row = {3} />
    </>
  );

}
